import React, { Component } from "react";
import Router from "next/router";
import { Mutation } from "react-apollo";
import gql from "graphql-tag";
import StyleComponent from "styled-components";
import { ProvidedRequiredArgumentsOnDirectivesRule } from "graphql/validation/rules/ProvidedRequiredArgumentsRule";

const Form = StyleComponent.form`
  display: grid;
  grid-template-column: 1fr;
  width: 200px
`;
const CREATE_USER = gql`
  mutation CRETE_USER($firstname: String, $lastname: String) {
    createUser(input: { firstname: $firstname, lastname: $lastname }) {
      id
      firstname
      lastname
    }
  }
`;

class User extends Component {
  state = {
    firstname: "Something",
    lastname: "Else",
  };

  handleChange = ({ target: { name, value } }) => {
    this.setState({ [name]: value });
  };

  render() {
    return (
      <Mutation mutation={CREATE_USER} variables={{ input: this.state }}>
        {(createUser, { loading, error }) => (
          <Form
            onSubmit={async (e) => {
              e.preventDefault();
              const user = await createUser();
              console.log(user);
            }}
          >
            {loading && <p> Loading !!!</p>}
            <label htmlFor="firstname">
              First name
              <input
                type="text"
                name="firstname"
                id="firstname"
                value={this.state.firstname}
                required
                onChange={this.handleChange}
              />
            </label>
            <label htmlFor="lastname">
              Last name
              <input
                type="text"
                name="lastname"
                id="lastname"
                value={this.state.lastname}
                required
                onChange={this.handleChange}
              />
            </label>
            <button type="submit">💆‍♂️ Create User</button>
          </Form>
        )}
      </Mutation>
    );
  }
}

export default User;
