import React, { Component } from "react";
import { Mutation } from "react-apollo";
import gql from "graphql-tag";
import StyleComponent from "styled-components";

const Form = StyleComponent.form`
  display: grid;
  grid-template-column: 1fr;
  width: 200px

  input {
    border-radius: 10px
  }

`;
// const CREATE_USER = gql`
//   mutation CRETE_USER(
//       $firstname: String!
//       $lastname: String!
//   ) {
//        createUser(
//          firstname: $firstname,
//          lastname: $$lastname
//        ) {
//          id
//          firstname
//          lastname
//        }
//     }
//   }
// `;

class User extends Component {
  state = {
    firstname: "",
    lastname: "",
  };

  handleChange = ({ target: { name, value } }) => {
    this.setState({ [name]: value });
  };
  render() {
    return (
      <Form>
        <label htmlFor="firstname">
          First name
          <input
            type="text"
            name="firstname"
            id="firstname"
            value={this.state.firstname}
            required
            onChange={this.handleChange}
          />
        </label>
        <label htmlFor="lastname">
          Last name
          <input
            type="text"
            name="lastname"
            id="lastname"
            value={this.state.lastname}
            required
            onChange={this.handleChange}
          />
        </label>
        <button type="submit">💆‍♂️ Create User</button>
      </Form>
    );
  }
}

export default User;
